package net.onlineshop.services.front.comment.bean;import java.io.Serializable;public class Comment extends net.onlineshop.services.common.Comment implements Serializable {	private String productName;	private static final long serialVersionUID = 1L;	public void clear() {		productName = null;		super.clear();	}	public String getProductName() {		return productName;	}	public void setProductName(String productName) {		this.productName = productName;	}}