package net.onlineshop.services.manage.cases.dao.impl;import java.util.List;import net.onlineshop.core.dao.BaseDao;import net.onlineshop.core.dao.page.PagerModel;import net.onlineshop.services.manage.cases.bean.Cases;import net.onlineshop.services.manage.cases.dao.CasesDao;public class CasesDaoImpl implements CasesDao {	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Cases e) {		return dao.selectPageList("manage.cases.selectPageList", "manage.cases.selectPageCount", e);	}	public List selectList(Cases e) {		return dao.selectList("manage.cases.selectList", e);	}	public Cases selectOne(Cases e) {		return (Cases) dao.selectOne("manage.cases.selectOne", e);	}	public int delete(Cases e) {		return dao.delete("manage.cases.delete", e);	}	public int update(Cases e) {		return dao.update("manage.cases.update", e);	}	public int deletes(String[] ids) {		Cases e = new Cases();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Cases e) {		return dao.insert("manage.cases.insert", e);	}	public int deleteById(int id) {		return dao.delete("manage.cases.deleteById", id);	}	@Override	public Cases selectById(String id) {		return (Cases) dao.selectOne("manage.cases.selectById", id);	}}