package net.onlineshop.services.manage.product;import java.util.List;import java.util.Map;import net.onlineshop.core.Services;import net.onlineshop.services.manage.product.bean.Product;public interface ProductService extends Services<Product> {	/**	 * 商品上架/下架	 * 	 * @param ids	 *            商品ID集合	 * @param status	 *            状态	 */	void updateProductStatus(String[] ids, int status, String updateAccount);	void updateStockAfterPaySuccess(Product product);	/**	 * 根据商品ID查询商品库存集合	 * 	 * @param productIDs	 * @return	 */	List<Product> selectStockByIDs(List<String> productIDs);	int selectOutOfStockProductCount();	void updateImg(Product p);	/**	 * 更新商品表，绑定活动ID	 */	void updateProductBindActivityId(Product pro);		public int updateSupplier(Map<String,Object> map) ;	/**	 * 更新商品表，清除某个已绑定的商品的活动ID	 * 	 * @param activityID	 */	void updateResetThisProductActivityID(String activityID);	public List selectGzidInfo(Product product);	public List selectSellInfo(Product product);}