package net.onlineshop.services.manage.spec.bean;import java.io.Serializable;import net.onlineshop.core.dao.page.PagerModel;import net.onlineshop.services.front.account.impl.ImageUtils;/** * 产品规格说明 * @author bing.wang * */public class Spec extends PagerModel implements Serializable {	private static final long serialVersionUID = 1L;	private String id;	private String productID;	private String specColor;	private String specSize;	private String specStock;	private String specPrice;	private String specStatus;	private String picurl;	private String picurl400;	private String picurl160;	private Integer sellCount;	/**	 * 规格的显示和隐藏	 */	public static final String spec_specStatus_y = "y";	public static final String spec_specStatus_n = "n";	public Spec() {		super();	}	public Spec(String id, String productID, String specColor, String specSize, String specStock, String specPrice, String specStatus, String picurl) {		super();		this.id = id;		this.productID = productID;		this.specColor = specColor;		this.specSize = specSize;		this.specStock = specStock;		this.specPrice = specPrice;		this.specStatus = specStatus;		this.picurl = picurl;	}	public void clear() {		super.clear();		id = null;		productID = null;		specColor = null;		specSize = null;		specStock = null;		specPrice = null;		specStatus = null;		picurl = null;		sellCount = null;	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getProductID() {		return productID;	}	public void setProductID(String productID) {		this.productID = productID;	}	public String getSpecColor() {		return specColor;	}	public void setSpecColor(String specColor) {		this.specColor = specColor;	}	public String getSpecSize() {		return specSize;	}	public void setSpecSize(String specSize) {		this.specSize = specSize;	}	public String getSpecStock() {		return specStock;	}	public void setSpecStock(String specStock) {		this.specStock = specStock;	}	public String getSpecPrice() {		return specPrice;	}	public void setSpecPrice(String specPrice) {		this.specPrice = specPrice;	}	public String getSpecStatus() {		return specStatus;	}	public void setSpecStatus(String specStatus) {		this.specStatus = specStatus;	}	@Override	public String toString() {		return "Spec [id=" + id + ", productID=" + productID + ", specColor=" + specColor + ", specSize=" + specSize + ", specStock=" + specStock + ", specPrice=" + specPrice + ", specStatus=" + specStatus + ", picurl=" + picurl + "]";	}	public String getPicurl() {		return picurl;	}	public void setPicurl(String picurl) {		this.picurl = picurl;	}	public String getPicurl160() {		if (this.getPicurl() != null && !"".equals(this.getPicurl())) {			picurl160 = ImageUtils.replaceLast(this.getPicurl(),"_3", "_1");		}		return picurl160;	}	public void setPicurl160(String picurl160) {		this.picurl160 = picurl160;	}	public String getPicurl400() {		if (this.getPicurl() != null && !"".equals(this.getPicurl())) {			picurl400 = ImageUtils.replaceLast(this.getPicurl(),"_3", "_2");		}		return picurl400;	}	public void setPicurl400(String picurl400) {		this.picurl400 = picurl400;	}	public Integer getSellCount() {		return sellCount;	}	public void setSellCount(Integer sellCount) {		this.sellCount = sellCount;	}}