package net.onlineshop.web.action.manage.cases;import net.onlineshop.core.BaseAction;import net.onlineshop.core.oscache.FrontCache;import net.onlineshop.services.manage.cases.CasesService;import net.onlineshop.services.manage.cases.bean.Cases;public class CasesAction extends BaseAction<Cases> {	private static final long serialVersionUID = 1L;	private CasesService casesService;	private FrontCache frontCache;	public FrontCache getFrontCache() {		return frontCache;	}	public void setFrontCache(FrontCache frontCache) {		this.frontCache = frontCache;	}	public CasesService getCasesService() {		return casesService;	}	protected void selectListAfter() {		pager.setPagerUrl("/manage/cases!selectList.action");	}	public void setCasesService(CasesService casesService) {		this.casesService = casesService;	}	public Cases getE() {		return this.e;	}	public String toAdd() throws Exception {		e.clear();		return toAdd;	}	public String toEdit() throws Exception {		e = getServer().selectOne(getE());		return toEdit;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new Cases();		}	}	public void insertAfter(Cases e) {		e.clear();	}	public String update() throws Exception {		getServer().update(getE());		String url = "/manage/cases!selectList.action";		insertAfter(getE());		this.getResponse().sendRedirect(url);		return null;	}	/**	 * 	 * 	 * @return	 * @throws Exception	 */	public String insert() throws Exception {		getServer().insert(getE());		String url = "/manage/cases!selectList.action";		insertAfter(getE());		this.getResponse().sendRedirect(url);		return null;	}}